//Q: Authnet Admin Tools
public function aat_doaction() {

	$json = array();
	$json['error'] = false;

	if (empty($this->request->post['aat_order_id'])) { $json['error'] = $this->language->get('error_order_id'); }
	if (empty($this->request->post['aat_action'])) { $json['error'] = $this->language->get('error_action'); }
	if (empty($this->request->post['aat_merchant_id'])) { $json['error'] = 'Error: Merchant ID required!'; }
	if (empty($this->request->post['aat_transaction_key'])) { $json['error'] = 'Error: Transaction Key required!'; }
	$classname = str_replace('vq2-admin_controller_sale_', '', basename(__FILE__, '.php'));

	if (!$json['error']) {

		// Save API details to settings db
		$savefields = array('aat_merchant_id', 'aat_transaction_key', 'aat_env');
		$savearr = array();
		foreach ($this->request->post as $key => $value) {
			if (in_array($key, $savefields)) {
				$savearr[$key] = $value;
			}
		}
		$this->load->model('setting/setting');
		$this->model_setting_setting->editSetting($classname, $savearr);
		//


		$query = $this->db->query("SELECT * FROM `" . DB_PREFIX . "authnetaim_admin` ana LEFT JOIN `" . DB_PREFIX . "order` o ON (ana.order_id = o.order_id) WHERE ana.order_id = '" . (int)$this->request->post['aat_order_id'] . "'");

		if (!$query->num_rows) { $json['error'] = 'Necessary transaction details missing. This order will need to be adjusted manually from your Authorize.net Account!'; }

		if (!$json['error']) {

			// Common Data setup:
			$data['x_login'] = $this->config->get('authorizenet_aim_login');
			$data['x_tran_key'] = $this->config->get('authorizenet_aim_key');
			$data['x_version'] = '3.1';
			$data['x_delim_data'] = 'true';
			$data['x_delim_char'] = ',';
			$data['x_encap_char'] = '"';
			$data['x_relay_response'] = 'false';
			$data['x_type'] = $this->request->post['aat_action'];

			// Specific Data setup:
			Switch ($this->request->post['aat_action']) {
				Case "CREDIT":
					if (empty($this->request->post['aat_amount'])) { $json['error'] = 'Amount Required!'; }
					if (empty($query->row['last_four'])) { $json['error'] = 'Last 4 digits not found on original order!'; }
					if (!$json['error']) {
						$data['x_amount'] = $this->request->post['aat_amount'];
						$data['x_card_num'] = $query->row['last_four'];
						$data['x_trans_id'] = $query->row['trans_id'];
						//$data['x_ref_trans_id'] = $query->row['trans_id'];
					}
					break;
				Case "PRIOR_AUTH_CAPTURE":
					if (!isset($query->row['auth_code'])) { $json['error'] = 'Auth Code not found on original order!'; }
					if (!$json['error']) {
						if (!empty($this->request->post['aat_amount'])) {
							$data['x_amount'] = $this->request->post['aat_amount'];
						}
						$data['x_auth_code'] = $query->row['auth_code'];
						$data['x_trans_id'] = $query->row['trans_id'];
					}
					break;
				Case "VOID":
					if (!isset($query->row['auth_code'])) { $json['error'] = 'Auth Code not found on original order!'; }
					if (!$json['error']) {
						$data['x_trans_id'] = $query->row['trans_id'];
					}
					break;
			}

			if (!$json['error']) {
				if ($this->config->get('authorizenet_aim_server') == 'live') {
					$url = 'https://secure.authorize.net/gateway/transact.dll'; // PROD
				} else {
					$url = 'https://test.authorize.net/gateway/transact.dll'; // DEV
				}

				$response = $this->curl_post($url, $data);

				$results = explode(',', $response['data']);

				foreach ($results as $i => $result) {
					if (trim($result, '"') != "") {
						$response_info[$i+1] = trim($result, '"');
					}
				}

				$json['sent'] = print_r($data, 1);
				$json['rcvd'] = print_r($response_info, 1);

				if ($response_info[1] == 1) {
					if ($this->request->post['aat_action'] == "PRIOR_AUTH_CAPTURE") {
						$auth_code = (isset($response_info['5'])) ? $response_info['5'] : 0;
						$this->db->query("UPDATE " . DB_PREFIX . "authnetaim_admin SET auth_code = '" . $this->db->escape($auth_code) . "' WHERE `order_id` = '" . (int)$this->request->post['aat_order_id'] . "'");
					}
					$xComment = ("Action: " . $this->request->post['aat_action'] . "\r\nResult: " . $response_info[4] . "\r\nRAW: " . print_r($response_info, 1));
					$order_status_info = $this->db->query("SELECT order_status_id FROM `" . DB_PREFIX . "order` WHERE order_id = '" . (int)$this->request->post['aat_order_id'] . "'");
					$this->db->query("INSERT INTO " . DB_PREFIX . "order_history SET order_id = '" . (int)$this->request->post['aat_order_id'] . "', order_status_id = '" . (int)$order_status_info->row['order_status_id'] . "', notify = '0', comment = '" . $this->db->escape($xComment) . "', date_added = NOW()");

					$this->load->model('sale/order');
$this->model_sale_order->updateOrderProductAndHistory($this->request->post['aat_order_id']);


					//$this->db->query("UPDATE " . DB_PREFIX . "order SET old_total=total, total_edits = total_edits+1 WHERE order_id = '" . (int)$this->request->post['aat_order_id'] . "'");

					$json['success'] = $response_info[4];
				} else {
					$json['error'] = $response_info[4];
				}
			}
		}
	}
	$this->response->setOutput(json_encode($json));
}

private function curl_post($url, $data) {
	$ch = curl_init($url);
	curl_setopt($ch, CURLOPT_PORT, 443);
	curl_setopt($ch, CURLOPT_HEADER, 0);
	curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);
	curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
	curl_setopt($ch, CURLOPT_FORBID_REUSE, 1);
	curl_setopt($ch, CURLOPT_FRESH_CONNECT, 1);
	curl_setopt($ch, CURLOPT_POST, 1);
	curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 10);
	curl_setopt($ch, CURLOPT_TIMEOUT, 10);
	curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data, '', '&'));
	$response = array();

	if (curl_error($ch)) {
		$response['error'] = curl_error($ch) . '(' . curl_errno($ch) . ')';
	} else {
		$response['data'] = curl_exec($ch);
	}
	curl_close($ch);

	return $response;
}
